            Municipality::create([
                'name' => $data['name'],
                'slug' => $data['slug'],
                'province_id' => $provinces[$data['province_slug']]->id,
                'latitude' => $data['latitude'],
                'longitude' => $data['longitude'],
            ]);
        }
    }
}
